import React, { useState, useEffect, searchParams } from 'react';

export function EditCustomer()
{
  const [customers, setCustomers] = useState([]);
  const [onLoadcustomers, setOnLoadCustomers] = useState([]);

    // Get a specific query parameter
    const myParam = searchParams.get('email');

    console.log(myParam);


    // on load, get all customers
    useEffect(async () => {
        const response = await fetch('/api/Customers');
        const data = await response.json();

        // on load customers
        setOnLoadCustomers(data);

        setCustomers(data)
        setLoading(false);
    }, []);
  


  const contents = loading
    ? <p><em>Loading...</em></p>
      :
      <>
        {searchElement()}
        {renderCustomersTable(customers)}
      </>

  return (
    <div>
      <h1 id="tableLabel">Edit Customers</h1>
      {contents}
    </div>
  );
}
