import React, { useState, useEffect } from 'react';

export function CustomerEdit()
{
    const [loading, setLoading] = useState(true);
    const [customer, setCustomer] = useState([]);

    // on load, get customer
    useEffect(async () => {

        // get the index from the url
        var index = parseInt(window.location.pathname.split('/')[2]);

        const response = await fetch('/Customers/GetCustomer/' + index);
        const data = await response.json();

        setCustomer(data)
        setLoading(false);

        console.log(data);
    }, []);


    const renderEditCustomerForm = (customer) => {
        return (
            <form>
                <div class="mb-3">
                    <label for="first_name" class="form-label">First Name</label>
                    <input type="text" class="form-control" id="first_name" value={customer.first_name} />
                </div>
                <div class="mb-3">
                    <label for="last_name" class="form-label">Last Name</label>
                    <input type="text" class="form-control" id="last_name" value={customer.last_name} />
                </div>
                <div class="mb-3">
                    <label for="email" class="form-label">Email</label>
                    <input type="text" class="form-control" id="email" value={customer.email} />
                </div>
                <div class="mb-3">
                    <label for="phone" class="form-label">Phone</label>
                    <input type="text" class="form-control" id="phone" value={customer.phone} />
                </div>
                <button type="submit" class="btn btn-primary">Submit</button>
            </form>
        );
    }

    const contents = loading
        ? <p><em>Loading...</em></p>
        :
        <>
            {renderEditCustomerForm(customer)}
        </>

  return (
    <div>
      <h1 id="tableLabel">Edit Customers</h1>
          {contents}
    </div>
  );
}
