import React, { useState, useEffect } from 'react';

export function Customers()
{
  const [loading, setLoading] = useState(true);
  const [customers, setCustomers] = useState([]);  

  useEffect(async () => {
    const response = await fetch('/api/Customers');
    const data = await response.json();

    setCustomers(data)
    setLoading(false);
  }, []);


  function onClickSearch()
  {

      // set customers to the search results
      var searchCustomers = customers.filter(customer => customer.first_name.toLowerCase().includes(document.getElementById("searchInput").value.toLowerCase()));

      console.log(searchCustomers);

      setCustomers(searchCustomers);
  }

  const searchElement = () => {
      return (
          <div class="input-group mb-3">
              <span class="input-group-text" id="button-addon1">Search By First Name</span>
              <input type="text" class="form-control" placeholder="Search" aria-label="Search" aria-describedby="button-addon2" id="searchInput" />
              <button class="btn btn-outline-secondary" type="button" id="button-addon2" onClick={onClickSearch} >Search</button>
          </div>
      );
  }

  const renderCustomersTable = (customerList) => {
      return (
      <div class="table-responsive">
              <table className='table align-middle table-striped' aria-labelledby="tableLabel">
            <thead>
              <tr>
                <th>First Name</th>
                <th>Last Name</th>
                <th>Phone</th>
                <th>Email</th>
              </tr>
            </thead>
            <tbody>
              {customerList.map(customer =>
                <tr key={customer.email}>
                      <td>{customer.first_name}</td>
                      <td>{customer.last_name}</td>
                      <td>{customer.phone}</td>
                      <td>{customer.email}</td>
                </tr>
              )}
            </tbody>
        </table>
      </div>
    );
  }

  const contents = loading
    ? <p><em>Loading...</em></p>
      :
      <>
        {searchElement()}
        {renderCustomersTable(customers)}
      </>

  return (
    <div>
          <h1 id="tableLabel">Customers</h1>

      {contents}
    </div>
  );
}
